var page = {
    init: function() {
        page.home_menu();
        page.scroTop();
        $("#magnifier").magnifier();
        page.swiper();
        page.onClick();
        page.swiperFn_3()
    },
    swiperFn_3: function() {
        var a = new Swiper(".swiper-container-3", {
            pagination: ".swiper-pagination",
            slidesPerView: 5,
            paginationClickable: true,
            spaceBetween: 13
        })
    },
    swiperFn_4: function() {
        var a = new Swiper(".swiper-container-4", {
            pagination: ".swiper-pagination",
            slidesPerView: 5,
            paginationClickable: true,
            spaceBetween: 13
        })
    },
    swiperFn_5: function() {
        var a = new Swiper(".swiper-container-5", {
            pagination: ".swiper-pagination",
            slidesPerView: 5,
            paginationClickable: true,
            spaceBetween: 13
        })
    },
    swiper: function() {
        var a = new Swiper(".swiper-container-1", {
            pagination: ".swiper-pagination",
            paginationClickable: true,
            nextButton: ".swiper-button-next",
            prevButton: ".swiper-button-prev",
            spaceBetween: 30
        });
        var a = new Swiper(".swiper-container-2", {
            pagination: ".swiper-pagination",
            paginationClickable: true,
            spaceBetween: 30
        })
    },
    scroTop: function() {
        $(window).scroll(function() {
            var b = $(window).height();
            var a = $(document).scrollTop();
            if (a >= b) {
                $(".top-search-hide").show()
            } else {
                $(".top-search-hide").hide()
            }
        })
    },
    home_menu: function() {
        $(".show-list").hover(function() {
            var b = $(this).find(".show-list-item").html();
            var c = parseInt($(this).attr("data-index"));
            var a = parseInt($(".menu-li").css("height"));
            switch (c) {
            case 1:
                $(this).find(".show-list-item").css("top", 0);
                break;
            case 2:
                $(this).find(".show-list-item").css("top", -a + "px");
                break;
            case 3:
                $(this).find(".show-list-item").css("top", -a * 2 + "px");
                break;
            case 4:
                $(this).find(".show-list-item").css("top", -a * 3 + "px");
                break;
            case 5:
                $(this).find(".show-list-item").css("top", -a * 4 + "px");
                break;
            case 6:
                $(this).find(".show-list-item").css("top", -a * 5 + "px");
                break;
            case 7:
                $(this).find(".show-list-item").css("top", -a * 6 + "px");
                break;
            case 8:
                $(this).find(".show-list-item").css("top", -a * 7 + "px");
                break;
            default:
            }
            $(this).addClass("active");
            $(this).siblings().removeClass("active");
            $(this).find(".show-list-item").show();
            $(this).siblings().find(".show-list-item").hide()
        },
        function() {
            $(this).find(".show-list-item").hide();
            $(this).removeClass("active")
        })
    },
    onClick: function() {
        $(".order-type li").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active")
        });
        $(".radio-change").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active");
            $(this).find(".radio-show").removeClass("hide");
            $(this).siblings().find(".radio-show").addClass("hide");
            $(this).find("input").attr("checked", "checked");
            $(this).siblings().find("input").attr("checked", false)
        });
        $(".nav-list li").on("click",
        function() {
            $(this).addClass("active").siblings("[class~='active']").removeClass("active")
        });
        $(".num").on("keyup",
        function() {
            var b = $(this).val();
            console.log(b);
            var a = "";
            if (/[^\d]/.test(b)) {
                var a = b.replace(/[^\d]/g, "")
            } else {
                if (/^[0]*/g.test(b)) {
                    var a = RegExp.rightContext
                }
            }
            $(this).val(a)
        });
        $(".add").on("click",
        function() {
            var a = parseInt($(this).siblings(".quantity-input").find(".num").val());
            if (isNaN(a)) {
                $(this).siblings(".quantity-input").find(".num").val("1")
            } else {
                a = a + 1;
                $(this).siblings(".quantity-input").find(".num").val(a)
            }
        });
        $(".min").on("click",
        function() {
            var a = parseInt($(this).siblings(".quantity-input").find(".num").val());
            if (a > 1) {
                a = a - 1;
                $(this).siblings(".quantity-input").find(".num").val(a)
            } else {
                if (a == 1) {
                    $(this).siblings(".quantity-input").find(".num").val("1")
                } else {
                    if (isNaN(a)) {
                        $(this).siblings(".quantity-input").find(".num").val("1")
                    }
                }
            }
        });
        $(".cart-del").on("click",
        function() {
            $(this).parent(".cart-item").remove()
        });
        $(".order-infor-item").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active")
        });
        $(".close-swt").on("click",
        function() {
            $(".swt-wrapper").hide()
        });
        $(".doc-ask-list").find("span").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active");
            var a = parseInt($(this).index());
            if (a == 3) {
                $(this).removeClass("active");
                $(this).siblings().toggle()
            }
        });
        $(".store-gooods").on("click",
        function() {
            $(this).toggleClass("active")
        });
        $(".close-resart-icon").on("click",
        function() {
            $(this).parent(".all-resart-item").hide(200);
            $(this).parent(".all-resart-item").prev().hide()
        });
        $(".cang").on("click",
        function() {
            $(this).toggleClass("active")
        });
        $(".size-list").find("em").on("click",
        function() {
            $(this).addClass("active");
            $(this).siblings().removeClass("active")
        });
        $(".all-resart-list").on("click",
        function() {
            if ($(this).hasClass("active")) {
                $(this).removeClass("active");
                $(".all-resart-hide").hide()
            } else {
                $(this).addClass("active");
                $(".all-resart-hide").show()
            }
        });
        $(".pay-style-item").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active");
            var a = parseInt($(this).index());
            if (a == 1) {
                $(".online-list").addClass("active")
            } else {
                $(".online-list").removeClass("active")
            }
        });
        $(".song-style").find("p").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active")
        });
        $(".pay-style-item2").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active")
        });
        $(".pay-style-item3").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active")
        });
        $(".help-menu-title").on("click",
        function() {
            $(this).toggleClass("active");
            $(this).next(".help-menu-list").toggle()
        });
        $(".efficacy-tab").find("a").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active");
            var a = parseInt($(this).index());
            $(".efficacy-cont-item").removeClass("active");
            $(".efficacy-cont-item").eq(a).addClass("active")
        });
        $(".tab-swiper-list").find("a").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active");
            var a = parseInt($(this).index());
            $(".tab-swiper-item").removeClass("active");
            $(".tab-swiper-item").eq(a).addClass("active");
            switch (a) {
            case 0:
                page.swiperFn_3();
                break;
            case 1:
                page.swiperFn_4();
                break;
            case 2:
                page.swiperFn_5();
                break;
            default:
            }
        });
        $(".p-detail-tab").find("a").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active");
            var a = parseInt($(this).index());
            $(".p-cont").removeClass("active");
            $(".p-cont").eq(a).addClass("active")
        });
        $(".radio-btn").on("click",
        function() {
            $(this).parent(".radio-span").addClass("active");
            $(this).parent(".radio-span").siblings().removeClass("active")
        });
        $(".page-list").find("a").on("click",
        function() {
            $(this).addClass("active").siblings().removeClass("active")
        });
        $(".consult-text").on("focus",
        function() {
            $(".consult-cont").css("height", "160px");
            $(this).css({
                width: "780px",
                height: "70px",
                padding: "10px",
                "margin-top": "10px"
            });
            $(".show-hide-icon").hide();
            $(".submit-consult").show()
        });
        $(".consult-text").on("blur",
        function() {
            $(".consult-cont").css("height", "36px");
            $(this).css({
                width: "740px",
                height: "36px",
                padding: "0 10px",
                "margin-top": "0"
            });
            $(".show-hide-icon").show();
            $(".submit-consult").hide()
        });
        $(".recommend-list").on("click",
        function() {
            $(this).addClass("active");
            $(this).siblings().removeClass("active");
            var a = parseInt($(this).attr("data-index"));
            switch (a) {
            case 1:
                $(".recommend-menu").css("borderColor", "#ea5252");
                break;
            case 2:
                $(".recommend-menu").css("borderColor", "#ed7033");
                break;
            case 3:
                $(".recommend-menu").css("borderColor", "#5eb14c");
                break;
            case 4:
                $(".recommend-menu").css("borderColor", "#3096f2");
                break
            }
        });
        $(".showall").on("click",
        function() {
            if ($(".show-content").hasClass("active")) {
                $(".like-care").animate({
                    "margin-top": "466px  "
                });
                $(".show-content").removeClass("active")
            } else {
                $(".like-care").animate({
                    "margin-top": "15px"
                });
                $(".show-content").addClass("active")
            }
        });
        $(".home-menu-inner").find("a").on("click",
        function() {
            var b = parseInt($(this).attr("data-index"));
            var a = $(this).width();
            switch (b) {
            case 1:
                $(".hover-move").animate({
                    left:
                    "32px"
                });
                break;
            case 2:
                $(".hover-move").animate({
                    left:
                    32 + a + "px"
                });
                break;
            case 3:
                $(".hover-move").animate({
                    left:
                    32 + a * 2 + "px"
                });
                break;
            case 4:
                $(".hover-move").animate({
                    left:
                    32 + a * 3 + "px"
                });
                break;
            case 5:
                $(".hover-move").animate({
                    left:
                    32 + a * 4 + "px"
                });
                break;
            case 6:
                $(".hover-move").animate({
                    left:
                    32 + a * 5 + "px"
                });
                break;
            case 7:
                $(".hover-move").animate({
                    left:
                    32 + a * 6 + "px"
                });
                break;
            default:
            }
        })
    }
};
page.init();